package worms.model.expressions;/** * A class of binary expressions, representing the addition of the * operand at the left-hand side and the operand at the right-hand * side. *  * @version  2.0 * @author   Eric Steegmans */public class Addition extends DoubleBinaryExpression {	/**	 * Initialize this new addition with given operands.	 *	 * @param  left	 *         The left operand for this new addition.	 * @param  right	 *         The right operand for this new addition.	 * @effect This new addition is initialized as a binary expression	 *         with the given operands.	 *       | super(left,right)	 */	public Addition(DoubleExpressions left, DoubleExpressions right)			throws IllegalOperandException {		super(left, right);	}	/**	 * Return the value of this addition.	 *	 * @return The sum of the values of the operands of this addition.	 *       | result ==	 *       |   getLeftOperand().getValue() + 	 *       |   getRightOperand().getValue()	 */	@Override	public double getValue() {		return getLeftOperand().getValue() + getRightOperand().getValue();	}	/**	 * Return the symbol representing the operator of this addition.	 * 	 * @return The string "+"	 *       | result.equals("+")	 */	@Override	public String getOperatorSymbol() {		return "+";	}}