package worms.model.expressions;import worms.model.types.DoubleType;public class Addition extends DoubleBinaryExpression {	public Addition(Expression left, Expression right)			throws IllegalOperandException {		super(left, right);	}	@Override	public DoubleType getValue() {		return new DoubleType(((Double) super.getLeftOperand().getValue().getValue()) + 				((Double) super.getRightOperand().getValue().getValue()));	}	@Override	public String getOperatorSymbol() {		return "+";	}	@Override	public String toString() {		return (getLeftOperand().toString() + getOperatorSymbol() + getRightOperand().toString());	}	@Override	public Addition clone() {		return new Addition(super.getLeftOperand(),super.getRightOperand());	}}