package worms.model.expressions;import be.kuleuven.cs.som.annotate.*;public abstract class DoubleBinaryExpression extends DoubleComposedExpression {	@Model	protected DoubleBinaryExpression(Expression left, Expression right)			throws IllegalOperandException {		leftOperand=left;		rightOperand=right;	}	@Basic	public Expression getLeftOperand() {		return leftOperand;	}		private Expression leftOperand;		@Basic	public Expression getRightOperand() {		return rightOperand;	}	private Expression rightOperand;}