package worms.model.expressions;import worms.model.types.DoubleType;public class Multiplication extends DoubleBinaryExpression {		public Multiplication(Expression left, Expression right)			throws IllegalOperandException {		super((DoubleExpression)left, (DoubleExpression)right);	}	@Override	public DoubleType getValue() {		return new DoubleType(((Double) getLeftOperand().getValue().getValue()) * 				((Double) getRightOperand().getValue().getValue()));	}	@Override	public String getOperatorSymbol() {		return "*";	}	@Override	public String toString() {		return (getLeftOperand().toString() + getOperatorSymbol() + getLeftOperand().toString());	}	@Override	public Multiplication clone() {		return new Multiplication(super.getLeftOperand(),super.getRightOperand());	}	}